
public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float a = 11.3f;
		float b = 22.2f;
		float c;
		
		c = a;
		a = b;
		b = c;
		
		System.out.println("The value of a is " + a + " and "+ " The value of b is "+ b );
		
		
		
	}

}
